import React, { useState } from 'react';
import project2 from '../assets/images/collage2.png';
import project1 from '../assets/images/collage1.png';

const Portfolio = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <section id="portfolio" className="portfolio-section p-0 pt-0 mt-0">
      <div className="latest-projects-section">
        <h2 className=""><span className="display-5">Latest Projects</span></h2>
        <div className="projects-container">
          <div className="project-item">
            <img src={project1} alt="Project 1" />
            <div className="project-content">
              <h3>Website Design for Marketing Agency Startup</h3>
              <p>I design and develop services for customers of all sizes, specializing in creating stylish, modern websites, web services and online stores.</p>
              <button className="view-project-button" onClick={openModal}>View Project</button>
            </div>
          </div>
          <div className="project-item">
            <div className="project-content">
              <h3>UX Agency Landing Page</h3>
              <p>Clean Design, Fully Customizable, Well-organized layers and groups.</p>
              <button className="view-project-button" onClick={openModal}>View Project</button>
            </div>
            <img src={project2} alt="Project 2" />
          </div>
        </div>
      </div>

      {modalVisible && (
        <div id="projectModal" className="portfolio-modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <img src={project1} alt="Project" className="modal-image" />
            <h2>Website Design for Marketing Agency Startup 01</h2>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit...</p>
            <p><strong>Type:</strong> Website</p>
            <p><strong>Languages:</strong> PHP, HTML, CSS, JS</p>
            <p><strong>Platform:</strong> WordPress</p>
            <p><strong>Country:</strong> USA</p>
            <p><strong>Live URL:</strong> <a href="http://www.example.com" target="_blank" rel="noopener noreferrer">http://www.example.com</a></p>
          </div>
        </div>
      )}
    </section>
  );
};

export default Portfolio;
