import React, { useState } from 'react';

import project2 from '../assets/images/collage2.png';
import project1 from '../assets/images/collage1.png';
// import project3 from '../assets/images/project3.png';

const Portfolio = () => {
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState({});

  const openModal = (content) => {
    setModalContent(content);
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
    setModalContent({});
  };

  const projects = [
    {
      title: 'Website Design for Marketing Agency Startup',
      description: 'I design and develop services for customers of all sizes, specializing in creating stylish, modern websites, web services and online stores.',
      imgSrc: project1,
      type: 'Website',
      languages: 'PHP, HTML, CSS, JS',
      platform: 'WordPress',
      country: 'USA',
      liveUrl: 'http://www.example.com'
    },
    {
      title: 'UX Agency Landing Page',
      description: 'Clean Design, Fully Customizable, Well-organized layers and groups.',
      imgSrc: project2,
      type: 'Landing Page',
      languages: 'HTML, CSS, JS',
      platform: 'Web',
      country: 'USA',
      liveUrl: 'http://www.example.com'
    },
    // Add more projects as needed
  ];

  return (
    <section id="portfolio" className="p-0 pt-0 mt-0">
      <div className="latest-projects-section">
        <h2 className=""><span className="display-5">Latest Projects</span></h2>
        <div className="projects-container">
          {projects.map((project, index) => (
            <div className="project-item" key={index}>
              <img src={project.imgSrc} alt={project.title} />
              <div className="project-content">
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <button
                  className="view-project-button"
                  onClick={() => openModal(project)}
                >
                  View Project
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {modalVisible && (
        <div id="projectModal" className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <img src={modalContent.imgSrc} alt="Project" className="modal-image" />
            <h2>{modalContent.title}</h2>
            <p>{modalContent.description}</p>
            <p><strong>Type:</strong> {modalContent.type}</p>
            <p><strong>Languages:</strong> {modalContent.languages}</p>
            <p><strong>Platform:</strong> {modalContent.platform}</p>
            <p><strong>Country:</strong> {modalContent.country}</p>
            <p><strong>Live URL:</strong> <a href={modalContent.liveUrl} target="_blank" rel="noopener noreferrer">{modalContent.liveUrl}</a></p>
          </div>
        </div>
      )}
    </section>
  );
};

export default Portfolio;
